mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY instruction_id) AS rn, instruction_id, recipe_name, output, instruction_date, instruction_employee, manufacturing_STATUS FROM manufacturingInstruction WHERE recipe_name LIKE '%'||#{manufacturingInstructionVO.recipe_Name}||'%'
AND MANUFACTURING_STATUS=0
AND MANUFACTURING_STATUS=1
AND MANUFACTURING_STATUS=2
)
WHERE rn <= #{paging.pageNum} * #{paging.pageSize} AND rn > (#{paging.pageNum} - 1) * #{paging.pageSize}

SELECT output, remainingProduction
FROM manufacturingInstruction
where instruction_Id = #{instruction_Id}
